package com.cy.yunsuanfuyubiaodashi;
/*	
 * 三元运算符的练习
 * 比较两个整数是否相同
 */
public class OperatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 定义两个int类型的变量
		int a = 10;
		int b = 20;

		boolean flag = (a == b) ? true : false;
		// boolean flag = (a == b);
		System.out.println(flag);
	}

}
